import React, {useState} from "react";
import './slider.css'
import {Favorite, MoreVert, Person} from "@mui/icons-material";
import {Users} from "../Share/Data";
import {useEffect} from "react";

function Slider({post}) {
    const [like, setLike] = useState(post.like);
    const [isLiked, setIsLiked] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredPosts, setFilteredPosts] = useState([]);

    const likeHandler = () => {
        setLike(isLiked ? like - 1 : like + 1);
        setIsLiked(!isLiked);
    };

    useEffect(() => {
        const filteredItems = [post].filter(
            (p) =>
                p.header.toLowerCase().includes(searchTerm.toLowerCase()) ||
                p.desc.toLowerCase().includes(searchTerm.toLowerCase())
        );
        setFilteredPosts(filteredItems);
    }, [searchTerm, post]);

    return (
        <>
            <div className="post-1">
                <div className="post-wrapper">
                    <div className="post-top">
                        <div className="post-top-left">
                            <Person/>
                            <span className="post-user-name">
                            {Users.filter(u => u.id === post.userId)[0].username}
                        </span>
                            <span className="post-date"> <p>{post.date}</p></span>
                        </div>
                        <div className="post-top-right">
                            <MoreVert/>
                        </div>
                    </div>
                    <div className="post-center">
          <span className="post-text">
            <h1>
              <strong>{filteredPosts.length > 0 ? filteredPosts[0].header : ""}</strong>
            </h1>
            <img className="post-image" src={filteredPosts.length > 0 ? filteredPosts[0].photo : ""} />
            <span>{filteredPosts.length > 0 ? filteredPosts[0].desc : ""}</span>
          </span>
                    </div>
                    <div className="post-bottom">
                        <div className="post-bottom-left">
                            <Favorite className={"like-button " + (isLiked ? "liked" : "")} onClick={likeHandler}></Favorite>
                            <span className="post-like-counter">{like} people like it</span>
                        </div>
                        <div className="post-bottom-right">
                            <span className="post-comment">{post.comment} comments</span>
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}
export default Slider;
