import React, {useState} from 'react'
import './ratings.css'
import {StarBorderOutlined} from "@mui/icons-material";

function Ratings(props){
    const [rating,setRating] = useState(null);
    const [hover, setHover] = useState(null);
    return(
        <div className="faculty-professors">
            <div className="professors">
                <img  className="faculty-image" src={props?.faculty?.img}/>
                <div className="faculty-info">
                    <h2>{props?.faculty?.name}</h2>
                    <h4>{props.faculty?.subject}</h4>
                    <div className="faculty-stars">
                        {[...Array(5)].map((star,index) => {
                            const currentRating = index + 1;
                            return(
                                <label>
                                    <input
                                        type="radio"
                                        name="rating"
                                        value={currentRating}
                                        onClick={() => setRating(currentRating)}
                                    >
                                        <StarBorderOutlined className="stars"></StarBorderOutlined>
                                    </input>
                                </label>
                            );
                        })}
                    </div>
                </div>
            </div>

        </div>
    )
}
export default Ratings
