import React , {useState} from 'react';
import './share.css'
import Slider from "../Slider/Slider";
import {Posts} from "./Data"
import {EmojiEmotions, Label, PermMedia, Room, Telegram} from "@mui/icons-material";
function Share({ searchTerm }){

    const [newPost, setNewPost] = useState({
        id: Posts.length + 1,
        userId: 1,
        header:"Listen in: To Build a Black Future",
        desc: "Listen to a sample chapter from To Build a Black Future – an incisive portrait of how the new Black politics can forge a future centered on collective action, community, and care.",
        photo: "https://press.princeton.edu/sites/default/files/styles/idea_embed/public/image/2023-09/Harris%20Audio%20Mockup%20Square_0.png?itok=5LzG7B4O",
        date: "September 14, 2023",
        like: 32,
        comment: 9
    });

    const handleAddPost = () => {
        // Add the new post to the Posts array
        Posts.push(newPost);
        // Clear the form after adding the post
        setNewPost({
            id: Posts.length + 1,
            userId: 1,
            header:"Listen in: To Build a Black Future",
            desc: "Listen to a sample chapter from To Build a Black Future – an incisive portrait of how the new Black politics can forge a future centered on collective action, community, and care.",
            photo: "https://press.princeton.edu/sites/default/files/styles/idea_embed/public/image/2023-09/Harris%20Audio%20Mockup%20Square_0.png?itok=5LzG7B4O",
            date: "September 14, 2023",
            like: 32,
            comment: 9
        });
    };
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewPost((prevPost) => ({
            ...prevPost,
            [name]: value,
        }));
    };

    const filteredPosts = Posts.filter((p) => {
        const lowerCaseHeader = p.header.toLowerCase();
        const lowerCaseDesc = p.desc.toLowerCase();
        const lowerCaseSearchTerm = searchTerm.toLowerCase();

        return lowerCaseHeader.includes(lowerCaseSearchTerm) || lowerCaseDesc.includes(lowerCaseSearchTerm);
    });

    return(
        <div className="share">
            <div className="share-wrapper">
                <div className="news-container">
                    <div className="news-wrapper">
                        <div className="news-top">
                            <h1>HomePage</h1>
                        </div>
                        <div className="news-bottom">
                            <div className="news-options">
                                <button className="news-button"><PermMedia className="news-icon"/></button>
                                <button className="news-button"><Label className="news-icon"/></button>
                                <button className="news-button"><Room className="news-icon"/></button>
                                <button className="news-button"><EmojiEmotions className="news-icon"/></button>
                            </div>
                            <button className="news-button"><Telegram/></button>
                        </div>
                    </div>
                    <div className="post-form">
                        {/* Form for adding new posts */}
                        <input
                            type="text"
                            placeholder="Post Header"
                            name="header"
                            value={newPost.header}
                            onChange={handleInputChange}
                        />
                        <textarea
                            placeholder="Post Description"
                            name="desc"
                            value={newPost.desc}
                            onChange={handleInputChange}
                        />
                        {/* Add more input fields as needed */}
                    </div>
                </div>
                {filteredPosts.map((p) => (
                    <Slider key={p.id} post={p} />
                ))}
            </div>
        </div>
    )
}
export default Share;
